import React from "react";

export default function Header(props) {

    const mainTextEl = document.getElementById("main-text-el");
    const fromTextEl = document.getElementById("from-text-el");
    const toTextEl = document.getElementById("to-text-el");

    function sendToDB() {
        let mainValue = mainTextEl.value;
        let fromValue = fromTextEl.value;
        let toValue = toTextEl.value;

        console.log(mainValue);
        console.log(fromValue);
        console.log(toValue);
    }


    return (
        <div className={"flow flow header-container"} style={{ "--flow-space": "0.75rem" }}>
            <img src={"champ-img.png"} alt={"Man holding up a microphone into the air."}/>
            <h1 className={"fs-500 ff-joesfin"}>We are the Champions!</h1>
            <textarea className={"bg-light radius border text-white main-text"} type={"textarea"} placeholder={"Some text here!"} id={"main-text-el"}></textarea>
            <div className={"flex justify-center"}>
                <input className={"bg-light radius border text-field text-white"} type={"text"} placeholder={"From"} id={"from-text-el"}></input>
                <input className={"bg-light radius border text-field text-white"} type={"text"} placeholder={"To"} id={"to-text-el"}></input>
            </div>
            <button className={"publish-btn"} id={"publish-btn"} onClick={sendToDB}>Publish!</button>
            <h2 className={"text-white ff-joesfin fs-500"}>- Endorsements -</h2>
        </div>
    )
}
