import Endorsement from './Endorsement';
import Header from './Header';
import { initializeApp } from "https://www.gstatic.com/firebasejs/9.15.0/firebase-app.js";
import { getDatabase, ref, push, onValue, remove } from "https://www.gstatic.com/firebasejs/9.15.0/firebase-database.js";
import React, {useState} from "react";

function App() {

    const appSettings = {
        databaseURL: "https://champions-project-e52d7-default-rtdb.firebaseio.com/"
    }
    const app = initializeApp(appSettings);
    const database = getDatabase(app);
    const endorsementsListInDB = ref(database, "endorsements");

    const [inputValue, setInputValue ] = useState({
        mainValue: "",
        fromValue: "",
        toValue: ""
    })

    const handleInputChange = (e) => {
        setInputValue(prevState => ({
            ...prevState,
            [e.target.name]: e.target.value,
        }));
    };

    function clearState() {
        setInputValue({
            mainValue: "",
            fromValue: "",
            toValue: "",
        })
    }
    function handleSubmit() {
        if (inputValue.mainValue > 0) {
            push(endorsementsListInDB, inputValue);
            clearState();
        } else {
            console.log("alert");
        }
    }

    function createComponent(id, main, from, to) {

    }

    onValue(endorsementsListInDB, function(snapshot) {
        if (snapshot.exists()) {
            const valuesArray = Object.entries(snapshot.val());

            endorsementElement = valuesArray.map(value => {
                return <Endorsement
                        id={value[0]}
                        mainText={value[1].mainValue}
                        fromText={value[1].fromValue}
                        toText={value[1].toValue}
                        />
            })
            // for (let item of valuesArray) {
            //     let currentEndorsementID = item[0];
            //     let currentMainText = item[1].mainValue;
            //     let currentFromText = item[1].fromValue;
            //     let currentToText = item[1].toValue;
            //
            //     createComponent(currentEndorsementID, currentMainText, currentFromText, currentToText);
        }
    })

  return (
    <div className="App">
        <div className={"container flow"}>
            <Header
                mainValue={inputValue.mainValue}
                fromValue={inputValue.fromValue}
                toValue={inputValue.toValue}
                handleChanges={handleInputChange}
                handleClick={handleSubmit}
            />
            <Endorsement />
            {endorsementElement}
        </div>
    </div>
  );
}

export default App;
