import Endorsement from './Endorsement';
import Header from './Header';
import { initializeApp } from "https://www.gstatic.com/firebasejs/9.15.0/firebase-app.js";
import { getDatabase, ref, push, onValue, remove } from "https://www.gstatic.com/firebasejs/9.15.0/firebase-database.js";
import React, {useState} from "react";

function App() {

    // Things I need to do
    // Step 1. Create and connect to Firebase Database

    const appSettings = {
        databaseURL: "https://champions-project-e52d7-default-rtdb.firebaseio.com/"
    }
    // const app = initializeApp(appSettings);
    // const endorsementsListInDB = ref(database, "endorsements");

    const [ mainValue, setMainValue ] = useState("");
    const [ fromValue, setFromValue ] = useState("");
    const [ toValue, setToValue ] = useState("");

    function handleValueChange() {
        setMainValue()
        setFromValue()
        setToValue()
    }
    const handleMainChange = (e) => {
        setMainValue(e.target.value);
    };

    const handleFromChange = (e) => {
        setFromValue(e.target.value);
    };

    const handleToChange = (e) => {
        setToValue(e.target.value);
    };


    console.log(mainValue)
    console.log(fromValue)
    console.log(toValue)

    //     Pull Data from Database and add to Endorsements Below.
    // Step 2. Grab Data from Input fields
    // Step 3. Add on click event handler to button.
    // Step 4. Send Data to Firebase
    // Step 5. Update Endorsements


  return (
    <div className="App">
        <div className={"container flow"}>
            <div className={"flow flow header-container"} style={{ "--flow-space": "0.75rem" }}>
                <img
                    src={"champ-img.png"}
                    alt={"Man holding up a microphone into the air."}
                />
                <h1 className={"fs-500 ff-joesfin"}>
                    We are the Champions!
                </h1>
                <textarea
                    className={"bg-light radius border text-white main-text"}
                    type={"textarea"}
                    placeholder={"Some text here!"}
                    id={"main-text-el"}
                    value={mainValue}
                    onChange={handleMainChange}
                ></textarea>
                <div className={"flex justify-center"}>
                    <input
                        className={"bg-light radius border text-field text-white"}
                        type={"text"}
                        placeholder={"From"}
                        id={"from-text-el"}
                        value={fromValue}
                        onChange={handleFromChange}
                    ></input>
                    <input
                        className={"bg-light radius border text-field text-white"}
                        type={"text"}
                        placeholder={"To"}
                        id={"to-text-el"}
                        value={toValue}
                        onChange={handleToChange}
                    ></input>
                </div>
                <button
                    className={"publish-btn"}
                    id={"publish-btn"}>
                    Publish!
                </button>
                <h2 className={"text-white ff-joesfin fs-500"}>- Endorsements -</h2>
            </div>
            <Endorsement />
        </div>
    </div>
  );
}

export default App;
